import { useEffect, useState } from "react";
import "./App.css";
import {Route,Routes,Link,Navigate} from "react-router-dom"
import Header from "./component/Header/Header"
import View from "./component/View/View"
import Main from "./component/Main/Main"
import About from "./component/About/About"
import Player from "./component/Player/Player";
import { useHistory } from "react-router-dom";
import axios from "axios"

function App(){
  const [para, setPara] = useState()
  const [player,setPlayer] = useState(1)
  const [player1,setPlayer1] = useState([0,0])
  const [player2,setPlayer2] = useState([0,0])
  const [player3,setPlayer3] = useState([0,0])
  const [player4,setPlayer4] = useState([0,0])
  const [turn,setTurn] = useState(1)
  const [price,setPrice] = useState()
    useEffect(() => {
      const options = {
        method: 'GET',
        url: 'https://www.random.org/integers/',
        headers: {
          // 'X-RapidAPI-Key': '9fb954e0d0msh1fcc78ff571276ap1e3d63jsnf114407345b7',
          // 'X-RapidAPI-Host': 'public-holiday.p.rapidapi.com'
        },
        params: {
          num:2,
          min:1,
          max:100,
          col:1,
          base:10,
          format:"plain",
          rnd:"new"
        }
      };

      axios.request(options).then(function (response) {
        console.log(response.data);
        let data = response.data.split("\n")
        data.pop()
        console.log(data);
        setPrice(data)
        console.log(price);
      }).catch(function (error) {
        console.error(error);
      });
  }, [])

    return (
      <div>
        <div>
        <nav>
          <Link to  = "/">
          <img
            src="https://en.bitcoin.it/w/images/en/2/29/BC_Logo_.png"
            alt=""
          />

          </Link>
          <Link to  = {"/game/"+player}>
          <h1>Beer Game</h1>
          </Link>

          <Link to  = "/about">
              About
          </Link>
        </nav>
        </div>
        <div>
          <Routes>
            <Route path="/" element={<Main />}/>
            <Route path="/about" element={<About />}/>
            <Route path="/game/:player" element={<Player player={player} setPlayer={setPlayer}/>}/>
            {/* <Route path="/currencies" element={<Currencies />}/> */}
            {/* <Route path="/price/:currency" element={<Price price={price} setPrice={setPrice}/>}/> */}
            <Route path="currency" element={<Navigate to = "/currencies"/>}/>
          </Routes>
        </div>
      </div>
    );
}

export default App;
