import "./Control.css";
import { redirect } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Control = (props) => {
  const navigate = useNavigate();
  // useEffect(() => {

  //   return (redirect("/player/"+props.player))
  // }, [])
  const [current, setCurrent] = useState();
  useEffect(() => {
    // console.log(current);
    switch (props.player) {
      case 1:
        props.setWhoPlay("Reatrailer");
        setCurrent(props.player1);
        break;
      case 2:
        props.setWhoPlay("Wholesale");
        setCurrent(props.player2);
        break;
      case 3:
        props.setWhoPlay("Distributor");
        setCurrent(props.player3);
        break;
      case 4:
        props.setWhoPlay("Manufactoring");
        setCurrent(props.player4);
        break;
      default:
        props.setWhoPlay("Reatrailer");
        setCurrent(props.player1);
        break;
    }
  });
  let data;
  let [data1,data2,data3,data4] =[]
  const editData = (e) => {
    data = current;
    data[3] = parseInt(e);
    switch (props.player) {
      case 1:
        props.setPlayer1(data);
        break;
      case 2:
        props.setPlayer2(data);
        break;
      case 3:
        props.setPlayer3(data);
        break;
      case 4:
        props.setPlayer4(data);
        break;
      default:
        props.setWhoPlay("Reatrailer");
        setCurrent(props.player1);
        break;
    }
  };
  const endTurn = () => {
    console.log(props.player1);
    console.log(props.player2);
    console.log(props.player3);
    console.log(props.player4);
    // edit order
    data1=props.player1
    data1 = turnData1(data1)
    props.setPlayer1(data1);
    data2=props.player2
    data2 = turnData2(data2,props.player1[4])
    props.setPlayer2(data2);
    data2=props.player2
    data3 = turnData3(data3,props.player2[4])
    props.setPlayer3(data3);
    data4=props.player4
    data4 = turnData2(data4,props.player3[4])
    props.setPlayer2(data4);
    // props.setPlayer2(data);
    // props.setPlayer3(data);
    // props.setPlayer4(data);
  };
  const turnData1 = (dataOfPlayer)=>{
    let stock=dataOfPlayer[0]
    let backlog=dataOfPlayer[1]
    let cost=dataOfPlayer[2]
    let order=dataOfPlayer[3]
    let sale=dataOfPlayer[4]
    sale = 15
    if(stock>order){
      stock= stock-sale
      sale = 0
    }else{
      stock=0
      backlog = Math.abs(stock-sale)
      sale = 0
    }
    cost=stock*1+backlog*3
    return ([stock,backlog,cost,sale,order])
  }
  const turnData2 = (dataOfPlayer,sale)=>{
    let stock=dataOfPlayer[0]
    let backlog=dataOfPlayer[1]
    let cost=dataOfPlayer[2]
    let order=dataOfPlayer[3]
    console.log(sale);
    if(stock>order){
      stock= stock-sale
      sale = 0
    }else{
      stock=0
      backlog = Math.abs(stock-sale)
      sale = 0
    }
    cost=stock*1+backlog*3
    return ([stock,backlog,cost,sale,order])
  }
  const turnData4 = (dataOfPlayer)=>{
    let stock=dataOfPlayer[0]
    let backlog=dataOfPlayer[1]
    let cost=dataOfPlayer[2]
    let order=dataOfPlayer[3]
    let sale=dataOfPlayer[4]
    // let manu=data1(5)
    sale = 15
    if(stock>order){
      order = 0
      stock= stock-sale
    }else{
      stock=0
      order =0
      backlog = Math.abs(stock-sale)
    }
    cost=stock*1+backlog*3
    return ([stock,backlog,cost,sale,order])
  }
  const isBacklog = () => {
    if (data[3] > data[0]) {
      data[0] = 0;
      data[3] = 0;
    } else {
    }
  };
  return (
    <div className="control-container">
      <div>
        <h1>
          Player {props.player} : {props.whoPlay}
        </h1>
      </div>
      <div className="turn-container">
        <h1>Turn : {props.turn}</h1>
        <h1>Week : {((props.turn - 1) % 4) + 1}</h1>
        <h1>Month : {Math.floor((props.turn - 1) / 4)}</h1>
      </div>
      <div className="input-value">
        <h1>1</h1>
        <div>
          <div>
            <h2>stock</h2>
            <h2></h2>
          </div>
          <div>
            <h2>BackLog</h2>
            <h2>0</h2>
          </div>
        </div>
        <h1>3</h1>
      </div>
      <div className="submit">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            props.setPlayer(props.player >= 4 ? 1 : props.player + 1);
            props.setTurn(props.player == 4 ? props.turn + 1 : props.turn);
            editData(e.target[0].value);
            document.getElementById("value").value = null;
            if(props.player == 4){
              endTurn()
            }
          }}
        >
          <input type="text" id="value" name="value"></input>
          <input type="submit" value="Submit"></input>
        </form>
      </div>
    </div>
  );
};

export default Control;
